import React from 'react';
import UpdateProduct from './UpdateProduct';
import DeleteProduct from './DeleteProduct';
import { aumentarCaixas, diminuirCaixas } from '../api';

const ProductList = ({ products, updateProducts }) => {

  const handleIncrease = async (id) => {
    await aumentarCaixas(id, 1);  // Aumenta 1 caixa
    updateProducts();  // Atualiza a lista após aumento
  };

  const handleDecrease = async (id) => {
    await diminuirCaixas(id, 1);  // Diminui 1 caixa
    updateProducts();  // Atualiza a lista após diminuição
  };

  return (
    <div>
      <h2>Product List</h2>
      <table>
        <thead>
          <tr>
            <th>Item</th>
            <th>Peso</th>
            <th>Quantidade em Estoque</th>
            <th>Quantidade Mínima em Estoque</th>
            <th>ID do Setor</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.id}>
              <td>{product.item}</td>
              <td>{product.peso} kg</td>
              <td>{product.quantidade_estoque}</td>
              <td>{product.quantidade_minima_estoque}</td>
              <td>{product.id_setor}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Ações colocadas abaixo da tabela */}
      <div className="actions">
        <button onClick={() => handleIncrease(products[0].id)}>Aumentar Caixas</button>
        <button onClick={() => handleDecrease(products[0].id)}>Diminuir Caixas</button>
        <UpdateProduct product={products[0]} updateProducts={updateProducts} />
        <DeleteProduct id={products[0].id} updateProducts={updateProducts} />
      </div>
    </div>
  );
};

export default ProductList;
