import React from 'react';
import UpdateProduct from './UpdateProduct';
import DeleteProduct from './DeleteProduct';
import { aumentarCaixas, diminuirCaixas, fetchProducts } from '../api';

const ProductList = ({ products, setProducts }) => {

  const handleIncrease = async (id) => {
    await aumentarCaixas(id, 1);  // Aumenta 1 caixa
    fetchProducts().then((response) => setProducts(response.data));
  };

  const handleDecrease = async (id) => {
    await diminuirCaixas(id, 1);  // Diminui 1 caixa
    fetchProducts().then((response) => setProducts(response.data));
  };

  return (
    <div>
      <h2>Product List</h2>
      <ul>
        {products.map((product) => (
          <li key={product.id}>
            {product.item} - {product.peso} kg - {product.numero_caixas} caixas
            <button onClick={() => handleIncrease(product.id)}>Aumentar Caixas</button>
            <button onClick={() => handleDecrease(product.id)}>Diminuir Caixas</button>
            <UpdateProduct product={product} setProducts={setProducts} />
            <DeleteProduct id={product.id} setProducts={setProducts} />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ProductList;
