import React, { useState, useEffect } from 'react';
import { fetchProducts } from './api';
import ProductList from './components/ProductList';
import AddProduct from './components/AddProduct';
import Dashboard from './components/Dashboard';  // Importando o componente de Dashboard

const App = () => {
  const [products, setProducts] = useState([]);

  // Função para atualizar os produtos
  const updateProducts = async () => {
    const response = await fetchProducts();
    setProducts(response.data);
  };

  useEffect(() => {
    updateProducts();  // Atualiza a lista ao carregar a página
  }, []);

  return (
<div className="App" style={{ width: '100%', height: '100vh' }}> {/* Define a altura e largura */}
  <h1>Product Management</h1>
  <AddProduct setProducts={setProducts} updateProducts={updateProducts} />
  <ProductList products={products} setProducts={setProducts} updateProducts={updateProducts} />
  
  <Dashboard products={products} />  {/* Passa os produtos para o dashboard */}
</div>
  );
};

export default App;
