import React, { useState } from 'react';
import { createProduct, fetchProducts } from '../api';

const AddProduct = ({ setProducts }) => {
  const [item, setItem] = useState('');
  const [peso, setPeso] = useState('');
  const [quantidadeEstoque, setquantidadeEstoque] = useState('');
  const [quantidadeMinimaEstoque, setquantidadeMinimaEstoque] = useState('');
  const [idSetor, setIdSetor] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newProduct = { item, peso: parseFloat(peso), quantidade_estoque: parseInt(quantidadeEstoque), quantidadeMinimaEstoque: parseInt(quantidadeMinimaEstoque), id_setor: parseInt(idSetor) };
    await createProduct(newProduct);
    fetchProducts().then((response) => setProducts(response.data));
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Add Product</h2>
      <input type="text" value={item} onChange={(e) => setItem(e.target.value)} placeholder="Item" required />
      <input type="number" value={peso} onChange={(e) => setPeso(e.target.value)} placeholder="Peso" required />
      <input type="number" value={quantidadeEstoque} onChange={(e) => setquantidadeEstoque(e.target.value)} placeholder="Quantidade estoque" required />
      <input type="number" value={quantidadeMinimaEstoque} onChange={(e) => setquantidadeMinimaEstoque(e.target.value)} placeholder="Quantidade minima estoque" required />
      <input type="number" value={idSetor} onChange={(e) => setIdSetor(e.target.value)} placeholder="ID do setor" required />
      <button type="submit">Add</button>
    </form>
  );
};

export default AddProduct;
