import React, { useState, useEffect } from 'react';
import { fetchProducts, createProduct, updateProduct, deleteProduct, aumentarCaixas, diminuirCaixas } from './api';
import ProductList from './components/ProductList';
import AddProduct from './components/AddProduct';

const App = () => {
  const [products, setProducts] = useState([]);

  // Função para buscar os produtos e atualizar a lista
  const updateProducts = async () => {
    const response = await fetchProducts();
    setProducts(response.data);  // Atualiza a lista de produtos
  };

  // Funções para operações de CRUD e aumento/diminuição de caixas
  const handleCreateProduct = async (product) => {
    await createProduct(product);
    updateProducts();  // Atualiza a lista após a criação
  };

  const handleUpdateProduct = async (id, updatedProduct) => {
    await updateProduct(id, updatedProduct);
    updateProducts();  // Atualiza a lista após a atualização
  };

  const handleDeleteProduct = async (id) => {
    await deleteProduct(id);
    updateProducts();  // Atualiza a lista após a exclusão
  };

  const handleIncrease = async (id) => {
    await aumentarCaixas(id, 1);  // Aumenta 1 caixa
    updateProducts();  // Atualiza a lista após o aumento
  };

  const handleDecrease = async (id) => {
    await diminuirCaixas(id, 1);  // Diminui 1 caixa
    updateProducts();  // Atualiza a lista após a diminuição
  };

  // Chama a função de busca de produtos ao carregar a página
  useEffect(() => {
    updateProducts();  // Busca os produtos ao carregar a página
  }, []);

  return (
    <div className="App">
      <h1>Product Management</h1>
      <AddProduct setProducts={setProducts} />
      <ProductList 
        products={products} 
        handleIncrease={handleIncrease} 
        handleDecrease={handleDecrease} 
        handleUpdateProduct={handleUpdateProduct} 
        handleDeleteProduct={handleDeleteProduct} 
      />
    </div>
  );
};

export default App;
