import React, { useState, useEffect } from 'react';
import { fetchProducts } from './api';
import ProductList from './components/ProductList';
import AddProduct from './components/AddProduct';
import { aumentarCaixas } from './api';

const handleIncrease = async (id, quantidade) => {
  try {
    const response = await aumentarCaixas(id, quantidade);
    console.log("Caixas aumentadas:", response.data);
  } catch (error) {
    console.error("Erro ao aumentar caixas:", error);
  }
};

// Em algum lugar do seu componente:
<button onClick={() => handleIncrease(1, 1)}>Aumentar Caixas do Produto 1</button>


const App = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetchProducts().then((response) => setProducts(response.data));
  }, []);

  return (
    <div className="App">
      <h1>Product Management</h1>
      <AddProduct setProducts={setProducts} />
      <ProductList products={products} setProducts={setProducts} />
    </div>
  );
};

export default App;
